{
    "class": "DO",
    "declaration": {
        "schemaVersion": "1.5.0",
        "class": "Device",
        "async": true,
        "Common": {
            "class": "Tenant",
            "hostname": "tenant1.appliance1.f5demo.net",
            "myProvision": {
                "class": "Provision",
                "avr": "nominal",
                "ltm": "nominal"
            },
            "myDns": {
                "class": "DNS",
                "nameServers": [
                    "192.168.10.0",
                    "192.168.10.1"
                ]
            },
            "myNtp": {
                "class": "NTP",
                "servers": [
                    "time.acme.com"
                ],
                "timezone": "UTC"
            },
            "internal-localself": {
                "class": "SelfIp",
                "address": "10.10.11.110/24",
                "vlan": "vlan-3010",
                "allowService": "all",
                "trafficGroup": "traffic-group-local-only"
            },
            "internal-self": {
                "class": "SelfIp",
                "address": "10.10.11.120/24",
                "vlan": "vlan-3010",
                "allowService": "all",
                "trafficGroup": "traffic-group-1"
            },
            "external-localself": {
                "class": "SelfIp",
                "address": "10.10.12.110/24",
                "vlan": "vlan-3011",
                "trafficGroup": "traffic-group-local-only",
                "allowService": "default"
            },
            "external-self": {
                "class": "SelfIp",
                "address": "10.10.12.120/24",
                "vlan": "vlan-3011",
                "trafficGroup": "traffic-group-1",
                "allowService": "default"
            },
            "ha-localself": {
                "class": "SelfIp",
                "address": "10.10.13.110/24",
                "vlan": "vlan-500",
                "trafficGroup": "traffic-group-local-only",
                "allowService": "default"
            },
            "configsync": {
                "class": "ConfigSync",
                "configsyncIp": "10.10.13.110"
            },
            "failoverAddress": {
                "class": "FailoverUnicast",
                "address": "10.10.13.110"
            },
            "failoverGroup": {
                "class": "DeviceGroup",
                "type": "sync-failover",
                "members": [
                    "10.10.13.110",
                    "10.10.13.244"
                ],
                "owner": "10.10.13.110",
                "autoSync": true,
                "saveOnAutoSync": false,
                "networkFailover": true,
                "fullLoadOnSync": false,
                "asmSync": false
            },
            "trust": {
                "class": "DeviceTrust",
                "localUsername": "{{Tenant_Username}}",
                "localPassword": "{{Tenant_Password}}",
                "remoteHost": "{{Appliance1_Tenant1_IP}}",
                "remoteUsername": "{{Tenant_Username}}",
                "remotePassword": "{{Tenant_Password}}"
            },
            "myDbVariables": {
                "class": "DbVariables",
                "ui.advisory.enabled": "true",
                "ui.advisory.color": "blue",
                "ui.advisory.text": "This is rSeries Tenant1 on Appliance1."
            },
            "admin": {
                "class": "User",
                "userType": "regular",
                "shell": "bash",
                "partitionAccess": {
                    "all-partitions": {
                        "role": "admin"
                    }
                },
                "password": "{{Tenant_Password}}"
            },
            "root": {
                "class": "User",
                "userType": "root",
                "newPassword": "{{Tenant_Password}}",
                "oldPassword": "{{Tenant_Password}}"
            }
        }
    },
    "targetHost": "{{Appliance1_Tenant1_IP}}",
    "targetUsername": "admin",
    "targetPassphrase": "admin",
    "bigIqSettings": {
        "failImportOnConflict": false,
        "conflictPolicy": "USE_BIGIQ",
        "deviceConflictPolicy": "USE_BIGIP",
        "versionedConflictPolicy": "KEEP_VERSION",
        "clusterName": "Tenant1-Cluster",
        "statsConfig": {
            "enabled": true,
            "zone": "default"
        },
        "snapshotWorkingConfig": false
    }
}

